import { Component, Input, OnInit, ElementRef, ViewChild, signal, HostBinding, effect } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Note, Workspace, Workspaces } from 'src/app/Data';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { ApiService } from 'src/app/services/api.service';


@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css']
})
export class BodyComponent implements OnInit {
  @ViewChild('scrollContainer',{static: false}) scrollContainer: ElementRef<HTMLDivElement> = {} as ElementRef<HTMLDivElement>;

  workspaces = signal<Workspaces>({})
  darkmode = signal<boolean>(
    JSON.parse(window.localStorage.getItem('darkmode')??'false')
  );

  constructor(private route: ActivatedRoute, private http: HttpClient, private api: ApiService) {
    effect(()=>{
      window.localStorage.setItem('darkmode',JSON.stringify(this.darkmode()))
    })
  }

  scrollH(event: any) {
    this.scrollContainer.nativeElement.scrollLeft+=event.deltaY/2;
    event.preventDefault()
  }

  ngOnInit() {
    this.route.params.subscribe(params => {
      this.api.curWsId.set(params['id']);
    });
    this.getWorkspace()
  }


  getWorkspace(){
    this.api.getWorkspace()
  }

  addNote(){
    this.api.addNote()
  }

  getWs(){
    return this.api.workspaces()
  }

  getCurWsId(){
    return this.api.curWsId()
  }

  @HostBinding('class.dark') get mode() {
    return this.darkmode();
  }

}

<div class="darkmode() h-screen bg-base-p text-font-base dark:bg-dark-p dark:text-font-dark">
    <app-header [darkmode]="darkmode"></app-header>
    <div *ngIf="this.getWs() as workspaces; else loading" class=" grid grid-cols-[minmax(0,1fr),minmax(0,4fr)]">
        <app-sidebar class="ml-4 my-8" [workspaces]="workspaces"></app-sidebar>
        <div (wheel)="scrollH($event)" class="h-[calc(100vh-5rem)]">
            <div #scrollContainer class="p-10 grid auto-cols-[calc((36vh-5.5rem)*1.3)] grid-flow-col grid-rows-2 gap-y-7 gap-x-16 w-full overflow-x-auto no-scrollbar">
                <app-card *ngFor="let note of workspaces[this.getCurWsId()].notes" [note]="note"></app-card>
                <div (click)="addNote()" class="cursor-pointer bg-base-s dark:bg-dark-s h-full aspect-square rounded-3xl font-bold flex justify-center items-center text-[10rem]">
                    <svg class="w-20 h-20 m-auto stroke-slate-500 opacity-50" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor">
                        <path stroke-linecap="round" stroke-linejoin="round" d="M12 4.5v15m7.5-7.5h-15" />
                    </svg>
                </div>
            </div>
        </div>
    </div>
    <ng-template #loading>
        <div class="w-full h-[calc(100%-5rem)] flex items-center justify-center m-auto"><app-spinner></app-spinner></div>
    </ng-template>
</div>
