import { Component, Input, ElementRef, ViewChild, signal, HostBinding, effect } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Note, Workspace, Workspaces } from 'src/app/Data';


@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css']
})
export class BodyComponent {
  @ViewChild('scrollContainer',{static: true}) scrollContainer: ElementRef<HTMLDivElement> = {} as ElementRef<HTMLDivElement>;

  addNote(){
    this.workspaces[this.id].notes.push({title:'',content:'',date: new Date()})
  }

  scrollH(event: any) {
    this.scrollContainer.nativeElement.scrollLeft+=event.deltaY/2;
    event.preventDefault()
  }
  id: string = "";

  ngOnInit() {
    this.route.params.subscribe(params => {
      this.id = params['id'];
    });
  }

  darkmode = signal<boolean>(
    JSON.parse(window.localStorage.getItem('darkmode')??'false')
  );

  Notes1: Note[] = [
    {
      title: 'Note 1',
      content: 'This is a note',
      date: new Date()
    },
    {
      title: 'Note 2',
      content: 'This is another note',
      date: new Date()
    },
    {
      title: 'Note 3',
      content: 'This is yet another note',
      date: new Date()
    }
  ]

  Notes2: Note[] = [
    {
      title: 'Note 90',
      content: 'This is a note',
      date: new Date()
    },
    {
      title: 'Note 100',
      content: 'This is another note',
      date: new Date()
    },
  ]

  Notes3: Note[] = [
    {
      title: 'Note 90',
      content: 'This is a note',
      date: new Date()
    },
  ]

  workspace1: Workspace = {
    name: 'Home',
    notes: this.Notes1
  }

  workspace2: Workspace = {
    name: 'Work',
    notes: this.Notes2
  }

  workspace3: Workspace = {
    name: 'Project 100',
    notes: this.Notes3
  }

  workspaces: Workspaces= {
    'idk': this.workspace1,
    'abc': this.workspace2,
    'xyz': this.workspace3
  }

  @HostBinding('class.dark') get mode() {
    return this.darkmode();
  }

  constructor(private route: ActivatedRoute) {
    effect(()=>{
      window.localStorage.setItem('darkmode',JSON.stringify(this.darkmode()))
    })
  }
}
