import { Component, Input } from '@angular/core';
import { Workspace } from 'src/app/Data';
import { AuthService } from '@auth0/auth0-angular';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css']
})
export class SidebarComponent {
  @Input() workspaces: { [key: string]: Workspace } = {};
  @Input() workspaceId: string = '';

  constructor(public auth: AuthService){

  }


}
<div class="pt-10 h-full border-2 rounded-3xl shadow-2xl border-slate-400 dark:border-slate-700 bg-base-s dark:bg-dark-s text-font-base dark:text-font-dark flex flex-col gap-5">
    <div routerLink="/workspace/{{workspace.key}}" [routerLinkActive]="'bg-white dark:bg-gray-950'" class="px-8 py-4 font-bold cursor-pointer bg-slate-300 dark:bg-gray-800 hover:bg-base-p hover:dark:bg-gray-950 w-11/12 h-fit mx-auto rounded-md" *ngFor="let workspace of workspaces | keyvalue" >{{workspace.value.name}}</div>
    <div class="px-8 py-4 font-bold cursor-pointer bg-slate-300 dark:bg-gray-800 hover:bg-base-p hover:dark:bg-dark-p w-11/12 h-fit mx-auto rounded-md">
        <!-- <img class="w-8 h-8 m-auto opacity-20" src="../../../assets/plus-circle.svg"> -->
        <svg class="w-8 h-8 m-auto stroke-slate-400 dark:stroke-slate-500 opacity-50" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor">
            <path stroke-linecap="round" stroke-linejoin="round" d="M12 4.5v15m7.5-7.5h-15" />
          </svg>
          
    </div>
    <div class="py-7 px-4" *ngIf="auth.isLoading$ | async; else loaded">
        <div role="status" class="max-w-sm animate-pulse">
            <div class="h-2.5 bg-base-p rounded-full dark:bg-dark-p w-32 mb-4"></div>
            <div class="h-2 bg-base-p rounded-full dark:bg-dark-p max-w-[240px] mb-2.5"></div>
            <span class="sr-only">Loading...</span>
        </div>
    </div>
    <ng-template #loaded>
        <div class="p-4" *ngIf="auth.user$ | async as user">
            <p class="font-bold text-lg">{{ user.name }}</p>
            <p class="text-slate-400 dark:text-slate-600">{{ user.email }}</p>
        </div>
    </ng-template>
</div>
