import { Injectable, signal } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http'
import { Workspaces } from '../Data';

@Injectable({
  providedIn: 'root'
})
export class ApiService {
  workspaces = signal<Workspaces>({});

  constructor(private http: HttpClient) { }

  getWorkspace(){
    this.http.get<any>('http://localhost:3000/api/getWorkspaces').subscribe(data=>{
      this.workspaces.set(data)
    })
  }

  addWorkspace(){
    this.http.put<any>('http://localhost:3000/api/addWorkspace',{
      name: 'New Space',
    }).subscribe(data=>{
      window.location.reload();
    })
  }

  updateName(workspaceId: string){
    this.http.put<any>('http://localhost:3000/api/updateWorkspaceName',{
      workspaceId: workspaceId,
      name: this.workspaces()[workspaceId].name,
    }).subscribe(data=>{
      window.location.reload();
    })
  }

  deleteWorkspace(workspaceId: string){
    this.http.put<any>('http://localhost:3000/api/deleteWorkspace',{
      workspaceId: workspaceId,
    }).subscribe(data=>{
      window.location.reload();
    })
  }

  addNote(){
    this.http.put<any>('http://localhost:3000/api/addNote',{
      workspaceId: this.id,
    }).subscribe(data=>{
      this.getWorkspace()
    })
  }

  updateTitle(workspaceId: string, noteId: string, title: string){
    this.http.put<any>('http://localhost:3000/api/updateNoteTitle',{
      workspaceId: workspaceId,
      noteId: noteId,
      title: title,
    }).subscribe(data=>{
      this.getWorkspace()
    })
  }

  updateContent(workspaceId: string, noteId: string, content: string){
    this.http.put<any>('http://localhost:3000/api/updateNoteContent',{
      workspaceId: workspaceId,
      noteId: noteId,
      content: content,
    }).subscribe(data=>{
      this.getWorkspace()
    })
  }

  deleteNote(){
    this.http.put<any>('http://localhost:3000/api/deleteNote',{
      workspaceId: this.id,
      noteId: this.id,
    }).subscribe(data=>{
      window.location.reload();
    })
  }
}
