import { Component, Input, OnInit } from '@angular/core';
import { Note } from 'src/app/Data';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})

export class CardComponent implements OnInit{
  @Input() note: Note = {} as Note;
  @Input() workspaceId: string = '';

  title:string = '';
  content: string = '';

  constructor(private http: HttpClient){}

  ngOnInit(){
    this.title = this.note.title;
    this.content = this.note.content;
  }

  updateTitle(event: any){
    this.http.put<any>('http://localhost:3000/api/updateNoteTitle',{
      workspaceId: this.workspaceId,
      noteId: this.note._id,
      title: this.title,
    }).subscribe(data=>{
      this.note.title = this.title;
    })
  }

  updateContent(event: any){
    this.http.put<any>('http://localhost:3000/api/updateNoteContent',{
      workspaceId: this.workspaceId,
      noteId: this.note._id,
      content: this.content,
    }).subscribe(data=>{
      this.note.content = this.content;
    })
  }  

  deleteNote(){
    this.http.put<any>('http://localhost:3000/api/deleteNote',{
      workspaceId: this.workspaceId,
      noteId: this.note._id,
    }).subscribe(data=>{
      window.location.reload();
    })
  }

}
