import { Component, Input, OnInit, ElementRef, ViewChild, signal, HostBinding, effect } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Note, Workspace, Workspaces } from 'src/app/Data';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Types } from 'mongoose';


@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css']
})
export class BodyComponent implements OnInit {
  @ViewChild('scrollContainer',{static: true}) scrollContainer: ElementRef<HTMLDivElement> = {} as ElementRef<HTMLDivElement>;

  id: string = "";
  // workspace: Workspace = {_id: new Types.ObjectId(),name:'',notes:[]};
  workspaces: Workspace[] = [];

  constructor(private route: ActivatedRoute, private http: HttpClient) {
    effect(()=>{
      window.localStorage.setItem('darkmode',JSON.stringify(this.darkmode()))
    })
  }

  ngOnInit() {
    this.route.params.subscribe(params => {
      this.id = params['id'];
    });

    // this.http.post<any>('http://localhost:3000/api/getWorkspace',{
    //   workspaceId: '6519700162fd5940bfb85aae'
    // }).subscribe(data=>{
    //   console.log(data)
    // })

    this.http.get<any>('http://localhost:3000/api/getWorkspaces').subscribe(data=>{
      this.workspaces=data
      console.log(this.workspaces[0].notes)
      for(let note of this.workspaces[0].notes){
        console.log(note)
      }
    })
  }

  addNote(){
    // this.workspaces[this.id].notes.push({_id: new Types.ObjectId,title:'',content:'',date: new Date()})

  }

  scrollH(event: any) {
    this.scrollContainer.nativeElement.scrollLeft+=event.deltaY/2;
    event.preventDefault()
  }

  darkmode = signal<boolean>(
    JSON.parse(window.localStorage.getItem('darkmode')??'false')
  );

  // Notes1: Note[] = [
  //   {
  //     title: 'Note 1',
  //     content: 'This is a note',
  //     date: new Date()
  //   },
  //   {
  //     title: 'Note 2',
  //     content: 'This is another note',
  //     date: new Date()
  //   },
  //   {
  //     title: 'Note 3',
  //     content: 'This is yet another note',
  //     date: new Date()
  //   }
  // ]

  // Notes2: Note[] = [
  //   {
  //     title: 'Note 90',
  //     content: 'This is a note',
  //     date: new Date()
  //   },
  //   {
  //     title: 'Note 100',
  //     content: 'This is another note',
  //     date: new Date()
  //   },
  // ]

  // Notes3: Note[] = [
  //   {
  //     title: 'Note 90',
  //     content: 'This is a note',
  //     date: new Date()
  //   },
  // ]

  // workspace1: Workspace = {
  //   name: 'Home',
  //   notes: this.Notes1
  // }

  // workspace2: Workspace = {
  //   name: 'Work',
  //   notes: this.Notes2
  // }

  // workspace3: Workspace = {
  //   _id: '',
  //   name: 'Project 100',
  //   notes: this.Notes3
  // }

  // workspace: Workspace= {
  //   'idk': this.workspace1,
  //   'abc': this.workspace2,
  //   'xyz': this.workspace3
  // }

  @HostBinding('class.dark') get mode() {
    return this.darkmode();
  }

}

<div class="darkmode() h-screen bg-base-p text-font-base dark:bg-dark-p dark:text-font-dark">
    <app-header [darkmode]="darkmode"></app-header>
    <div class=" grid grid-cols-[minmax(0,1fr),minmax(0,4fr)]">
        <app-sidebar class="ml-4 my-8" [workspaces]="workspaces"></app-sidebar>
        <div (wheel)="scrollH($event)" class="h-[calc(100vh-5rem)]">
            <div #scrollContainer class="p-10 grid auto-cols-[calc((36vh-5rem)*1.3)] grid-flow-col grid-rows-2 gap-y-7 gap-x-16 w-full overflow-x-auto no-scrollbar">
                <!-- <app-card *ngFor="let note of workspaces[0].notes" [note]="note"></app-card> -->
                <div *ngFor="let note of workspaces[0].notes">{{note.title}}</div>
                <div (click)="addNote()" class="cursor-pointer bg-base-s dark:bg-dark-s h-full aspect-square rounded-3xl font-bold flex justify-center items-center text-[10rem]">
                    <img class="opacity-10 w-20" src="../../../assets/plus-circle.svg">
                </div>
            </div>
        </div>
    </div>
</div>
